
const BtnOpen=document.querySelector('.burger-open');const BtnClose=document.querySelector('.burger-close');const burger=document.querySelector('.burger');const mainPage=document.querySelector('#main-page');const burgerBack=document.querySelector('.burger-back');BtnOpen.addEventListener('click',function(){burger.classList.add('open');mainPage.style.overflow='hidden';burgerBack.classList.add('show');})
BtnClose.addEventListener('click',function(){burger.classList.remove('open');burgerBack.classList.remove('show');})
